import React, { useState } from 'react';
import { FaHome, FaBuilding, FaUserFriends,FaMapMarkerAlt, FaLocationArrow } from 'react-icons/fa'; // Importing icons
import '../styles/addressForm.css';

const AddressForm = ({ location, onSave }) => {
  const [houseNo, setHouseNo] = useState('');
  const [area, setArea] = useState('');
  const [category, setCategory] = useState('Home');

  const handleSubmit = (e) => {
    e.preventDefault();
    const addressData = { houseNo, area, category, location };
    onSave(addressData); // Send the address data back to parent to be saved
  };

  return (
    <form onSubmit={handleSubmit} className="address-form">
      {/* Location Display */}
      <div className="location-container">
        <FaMapMarkerAlt className="location-icon" /> {/* Red location icon */}
        <p className="location">{location}</p>

        <p className="location-text">{location ? location : 'No location selected'}</p>
      </div>

      {/* House/Flat/Block No. */}
      <div className="input-container">
        <label>HOUSE / FLAT / BLOCK NO.</label>
        <input
          type="text"
          value={houseNo}
          onChange={(e) => setHouseNo(e.target.value)}
          className="input-line"
          required
        />
      </div>

      {/* Apartment/Road/Area */}
      <div className="input-container">
        <label>APARTMENT / ROAD / AREA</label>
        <input
          type="text"
          value={area}
          onChange={(e) => setArea(e.target.value)}
          className="input-line"
          required
        />
      </div>

      {/* Save As Category */}
      <div className="category-container">
        <label>SAVE AS</label>
        <div className="category-icons">
          <div
            className={`icon ${category === 'Home' ? 'selected' : ''}`}
            onClick={() => setCategory('Home')}
          >
            <FaHome />
          </div>
          <div
            className={`icon ${category === 'Office' ? 'selected' : ''}`}
            onClick={() => setCategory('Office')}
          >
            <FaBuilding />
          </div>
          <div
            className={`icon ${category === 'Friends & Family' ? 'selected' : ''}`}
            onClick={() => setCategory('Friends & Family')}
          >
            <FaUserFriends />
          </div>
          <div
            className={`icon ${category === 'Others' ? 'selected' : ''}`}
            onClick={() => setCategory('Others')}
          >
            <FaMapMarkerAlt />
          </div>
        </div>
      </div>

      {/* Save Button */}
      <button type="submit" className="save-button">Save Address</button>
    </form>
  );
};

export default AddressForm;
