import React, { useState } from 'react';
import { FaMapMarkerAlt } from 'react-icons/fa'; // Importing location icon
import '../styles/manageAddress.css';

const ManageAddresses = () => {
  const [addresses, setAddresses] = useState([
    { id: 1, type: 'Home', address: '123 Home St, Cityville' },
    { id: 2, type: 'Office', address: '456 Work Rd, Metropolis' },
    { id: 3, type: 'Friends & Family', address: '789 Friend Ave, Townsville' },
  ]);
  const [searchTerm, setSearchTerm] = useState('');

  const handleDelete = (id) => {
    setAddresses(addresses.filter((address) => address.id !== id));
  };

  const handleSearch = (e) => {
    setSearchTerm(e.target.value);
  };

  return (
    <div className="manage-addresses">
      {/* Search Bar Section */}
      <div className="search-bar">
        <input
          type="text"
          placeholder="Search your area / pincode/ apartment"
          value={searchTerm}
          onChange={handleSearch}
        />
      </div>

      {/* Current Location and Enable Button */}
      <div className="location-section">
        <div className="location-info">
          <FaMapMarkerAlt color="red" />
          <span className="current-location">Current Location</span>
        </div>
        <button className="enable-btn">Enable</button>
      </div>

      <h2>Manage Addresses</h2>
      <ul>
        {addresses.map((address) => (
          <li key={address.id} className="address-item">
            <div>
              <strong>{address.type}</strong>
              <p>{address.address}</p>
            </div>
            <button className="delete-btn" onClick={() => handleDelete(address.id)}>Delete</button>
          </li>
        ))}
      </ul>
      <button className="add-address-btn">Add New Address</button>
    </div>
  );
};

export default ManageAddresses;
