import React, { useState } from 'react';
import '../styles/addressForm.css'; // Import CSS for styling

const AddressForm = ({ onSave }) => {
  const [houseNo, setHouseNo] = useState('');
  const [area, setArea] = useState('');
  const [category, setCategory] = useState('Home');
  
  const handleCategoryChange = (newCategory) => {
    setCategory(newCategory);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (houseNo && area) {
      onSave({ houseNo, area, category });
    } else {
      alert('Please fill all the fields');
    }
  };

  return (
    <div className="address-form-container">
      <h2>Save Your Address</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>House/Flat/Block No.</label>
          <input
            type="text"
            value={houseNo}
            onChange={(e) => setHouseNo(e.target.value)}
            placeholder="Enter house number"
          />
        </div>
        <div className="form-group">
          <label>Apartment/Road/Area</label>
          <input
            type="text"
            value={area}
            onChange={(e) => setArea(e.target.value)}
            placeholder="Enter area"
          />
        </div>
        <div className="category-selection">
          <p>Select Address Category</p>
          <div className="category-icons">
            <div
              className={`category-icon ${category === 'Home' ? 'active' : ''}`}
              onClick={() => handleCategoryChange('Home')}
            >
              <span role="img" aria-label="Home">🏠</span>
              <p>Home</p>
            </div>
            <div
              className={`category-icon ${category === 'Office' ? 'active' : ''}`}
              onClick={() => handleCategoryChange('Office')}
            >
              <span role="img" aria-label="Office">💼</span>
              <p>Office</p>
            </div>
            <div
              className={`category-icon ${category === 'Friends & Family' ? 'active' : ''}`}
              onClick={() => handleCategoryChange('Friends & Family')}
            >
              <span role="img" aria-label="Friends & Family">👨‍👩‍👧‍👦</span>
              <p>Friends & Family</p>
            </div>
          </div>
        </div>
        <button type="submit" className="save-btn">Save Address</button>
      </form>
    </div>
  );
};

export default AddressForm;
