import React, { useState } from 'react';
import MapPicker from './components/mapPicker';
import LocationModal from './components/locationModal';
import useGeolocation from './hooks/useGeolocation';
import AddressForm from './components/addressForm'; // Import AddressForm component
import './App.css'; // Ensure App-wide styles are included

const App = () => {
  const { position, error } = useGeolocation();
  const [addresses, setAddresses] = useState([]); // State to hold saved addresses

  // Handle saving the address
  const handleSaveAddress = (newAddress) => {
    setAddresses([...addresses, newAddress]);
    alert('Address saved successfully!');
  };

  const handleEnableLocation = () => {
    if (!position) alert('Location access denied!');
  };

  const handleManualSearch = () => {
    console.log('Search manually triggered');
  };

  return (
    <div className="app-container">
      <LocationModal onEnable={handleEnableLocation} onManualSearch={handleManualSearch} />

      {/* MapPicker to allow user to pick a location */}
      <MapPicker onLocationSelect={(loc) => console.log(loc)} />

      {/* Address Form for entering and saving addresses */}
      <AddressForm onSave={handleSaveAddress} />

      {/* Display the saved addresses */}
      <div className="address-list">
        <h2>Saved Addresses</h2>
        {addresses.length > 0 ? (
          <ul>
            {addresses.map((address, index) => (
              <li key={index}>
                <p><strong>{address.category}:</strong> {address.houseNo}, {address.area}</p>
              </li>
            ))}
          </ul>
        ) : (
          <p>No addresses saved yet.</p>
        )}
      </div>
    </div>
  );
};

export default App;
