import React, { useState, useEffect } from 'react';
import MapPicker from './components/MapPicker';
import LocationModal from './components/LocationModal';
import useGeolocation from './hooks/useGeolocation';

const App = () => {
  const { position, error } = useGeolocation();
  const [showModal, setShowModal] = useState(true);
  const [selectedLocation, setSelectedLocation] = useState(null);

  // Handle enabling location (requesting browser permissions)
  const handleEnableLocation = () => {
    if (position) {
      setSelectedLocation({
        lat: position.coords.latitude,
        lng: position.coords.longitude,
      });
      setShowModal(false); // Close the modal once location is granted
    } else {
      alert('Location permission denied or unavailable.');
    }
  };

  // Handle manual search (trigger map or input field for address search)
  const handleManualSearch = () => {
    setShowModal(false); // Close the modal for manual search
  };

  // Handle the selected location from the map
  const handleLocationSelect = (loc) => {
    setSelectedLocation(loc);
  };

  return (
    <div>
      {showModal && <LocationModal onEnable={handleEnableLocation} onManualSearch={handleManualSearch} />}
      <MapPicker onLocationSelect={handleLocationSelect} initialLocation={selectedLocation} />
      {selectedLocation && (
        <div className="selected-location">
          <p>Selected Location:</p>
          <p>Lat: {selectedLocation.lat}</p>
          <p>Lng: {selectedLocation.lng}</p>
        </div>
      )}
    </div>
  );
};

export default App;
