import React, { useState } from 'react';
import { GoogleMap, Marker, useJsApiLoader } from '@react-google-maps/api';
import { FaHome, FaBuilding, FaUsers } from 'react-icons/fa';
import { FaLocationCrosshairs } from 'react-icons/fa6';
import '../styles/manageAddress.css';

const ManageAddresses = () => {
  const [mapCenter, setMapCenter] = useState({ lat: 37.7749, lng: -122.4194 }); // Default to San Francisco
  const [markerPosition, setMarkerPosition] = useState({ lat: 37.7749, lng: -122.4194 });
  const [addresses, setAddresses] = useState([
    { id: 1, type: 'Home', address: '123 Home St, Cityville', icon: <FaHome color="black" /> },
    { id: 2, type: 'Office', address: '456 Work Rd, Metropolis', icon: <FaBuilding color="black" /> },
    { id: 3, type: 'Friends & Family', address: '789 Friend Ave, Townsville', icon: <FaUsers color="black" /> },
  ]);

  const { isLoaded } = useJsApiLoader({
    googleMapsApiKey: 'YOUR_API_KEY', // Replace with your API key
  });

  const handleEnableLocation = () => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const { latitude, longitude } = position.coords;
          setMapCenter({ lat: latitude, lng: longitude });
          setMarkerPosition({ lat: latitude, lng: longitude });
        },
        (error) => {
          console.error('Error fetching location:', error);
          alert('Failed to fetch location. Please enable location services.');
        }
      );
    } else {
      alert('Geolocation is not supported by your browser.');
    }
  };

  if (!isLoaded) return <div>Loading Map...</div>;

  return (
    <div className="manage-addresses">
      {/* Search Bar Section */}
      <div className="search-bar">
        <input
          type="text"
          placeholder="Search your area / pincode / apartment"
        />
      </div>

      {/* Current Location and Enable Button */}
      <div className="location-section">
        <div className="location-info">
          <FaLocationCrosshairs color="red" />
          <span className="current-location">Current Location</span>
        </div>
        <button className="enable-btn" onClick={handleEnableLocation}>Enable</button>
        <div className="underline"></div>
      </div>

      {/* Google Map */}
      <div className="map-container">
        <GoogleMap
          center={mapCenter}
          zoom={15}
          mapContainerStyle={{ width: '100%', height: '400px' }}
        >
          <Marker position={markerPosition} />
        </GoogleMap>
      </div>

      {/* Saved Location Section */}
      <div className="saved-location">
        <h3>Saved Location</h3>
        <ul>
          {addresses.map((address) => (
            <li key={address.id} className="address-item">
              <div className="address-info">
                <div className="address-type">
                  {address.icon}
                  <strong>{address.type}</strong>
                </div>
                <p className="address-text">{address.address}</p>
              </div>
            </li>
          ))}
        </ul>
        <div className="underline"></div>
      </div>
    </div>
  );
};

export default ManageAddresses;
