const ManageAddresses = () => {
  const [addresses, setAddresses] = useState([
    { id: 1, type: 'Home', address: '123 Home St, Cityville', icon: <FaHome color="black" /> },
    { id: 2, type: 'Office', address: '456 Work Rd, Metropolis', icon: <FaBuilding color="black" /> },
    { id: 3, type: 'Friends & Family', address: '789 Friend Ave, Townsville', icon: <FaUsers color="black" /> },
  ]);
  
  const [recentSearches, setRecentSearches] = useState([]);
  const [mapCenter, setMapCenter] = useState({ lat: 37.7749, lng: -122.4194 }); // Default location
  const [searchTerm, setSearchTerm] = useState('');

  const { isLoaded } = useJsApiLoader({
    googleMapsApiKey: 'YOUR_API_KEY', // Replace with your API key
    libraries: ['places'], // Necessary for address search
  });

  const handleSearchLocation = async () => {
    const address = prompt("Enter the address:");
    if (!address) return;

    try {
      const geocodeUrl = `https://maps.googleapis.com/maps/api/geocode/json?address=${encodeURIComponent(address)}&key=YOUR_API_KEY`;
      const response = await fetch(geocodeUrl);
      const data = await response.json();

      if (data.status === "OK" && data.results.length > 0) {
        const location = data.results[0].geometry.location;
        setMapCenter(location);
        setRecentSearches((prev) => [...prev, { id: Date.now(), type: "Search", address }]);
      } else {
        alert("Address not found. Please try again.");
      }
    } catch (error) {
      console.error("Error fetching geocode:", error);
      alert("Failed to fetch location. Please check your internet connection or API key.");
    }
  };

  return (
    <div className="manage-addresses">
      {/* Search Bar Section */}
      <div className="search-bar">
        <input
          type="text"
          placeholder="Search your area / pincode / apartment"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
      </div>

      {/* Current Location and Enable Button */}
      <div className="location-section">
        <div className="location-info">
          <FaLocationCrosshairs color="red" size={24} />
          <span className="current-location">Current Location</span>
        </div>
        <button className="enable-btn">Enable</button>
        <div className="underline"></div>
      </div>

      {/* Search Location Button */}
      <button className="search-location-btn" onClick={handleSearchLocation}>
        Search Location
      </button>

      {/* Map Section */}
      <div className="map-container">
        {isLoaded ? (
          <GoogleMap
            center={mapCenter}
            zoom={14}
            mapContainerStyle={{ width: "100%", height: "400px" }}
          >
            <Marker position={mapCenter} />
          </GoogleMap>
        ) : (
          <p>Loading Map...</p>
        )}
      </div>

      {/* Saved Location Section */}
      <div className="saved-location">
        <h3>Saved Location</h3>
        <ul>
          {addresses.map((address) => (
            <li key={address.id} className="address-item">
              <div className="address-info">
                {/* Address Type with Icon */}
                <div className="address-type">
                  {address.icon}
                  <strong>{address.type}</strong>
                </div>
                {/* Address below the Type */}
                <p className="address-text">{address.address}</p>
              </div>
            </li>
          ))}
        </ul>
        <div className="underline"></div>
      </div>

      {/* Recent Searches Section */}
      <div className="recent-searches">
        <h3>Recent Searches</h3>
        <ul>
          {recentSearches.map((search) => (
            <li key={search.id} className="address-item">
              <div className="address-info">
                {/* Address Type with Icon */}
                <div className="address-type">
                  <FaMapMarkerAlt color="red" />
                  <strong>{search.type}</strong>
                </div>
                {/* Address below the Type */}
                <p className="address-text">{search.address}</p>
              </div>
            </li>
          ))}
        </ul>
        <div className="underline"></div>
      </div>
    </div>
  );
};

export default ManageAddresses;
